// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./libs/Types.sol";
import "./interfaces/ILoanManager.sol";

/**
 * @title LoanManager
 * @dev Manages loan lifecycle and accounting for MicroCredit pools
 */
contract LoanManager is ILoanManager, AccessControl {
    bytes32 public constant POOL_ROLE = keccak256("POOL_ROLE");

    // Loan counter and mapping
    uint256 private _loanCounter;
    mapping(uint256 => Types.Loan) public loans;

    // Events
    event LoanCreated(
        uint256 indexed loanId,
        address indexed borrower,
        uint256 amount,
        uint16 rateBps,
        uint16 tenorDays
    );
    event LoanStatusChanged(
        uint256 indexed loanId,
        Types.LoanStatus from,
        Types.LoanStatus to
    );
    event LoanAccountingUpdated(
        uint256 indexed loanId,
        uint256 interestPaid,
        uint256 principalPaid,
        uint256 remaining
    );

    // Custom errors
    error Unauthorized();
    error InvalidLoanId();
    error InvalidState();
    error AmountZero();

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new loan request
     * @param borrower Address of the borrower
     * @param amount Principal amount requested
     * @param rateBps Interest rate in basis points
     * @param tenorDays Loan term in days
     * @return loanId Unique identifier for the loan
     */
    function createLoan(
        address borrower,
        uint256 amount,
        uint16 rateBps,
        uint16 tenorDays
    ) external onlyRole(POOL_ROLE) returns (uint256 loanId) {
        if (amount == 0) revert AmountZero();
        if (borrower == address(0)) revert InvalidState();

        loanId = _loanCounter++;

        loans[loanId] = Types.Loan({
            borrower: borrower,
            principal: amount,
            createdAt: block.timestamp,
            drawnAt: 0,
            dueAt: 0,
            rateBps: rateBps,
            tenorDays: tenorDays,
            status: Types.LoanStatus.Requested
        });

        emit LoanCreated(loanId, borrower, amount, rateBps, tenorDays);
    }

    /**
     * @dev Approve a loan request
     * @param loanId ID of the loan to approve
     */
    function approve(uint256 loanId) external onlyRole(POOL_ROLE) {
        Types.Loan storage loan = loans[loanId];
        if (loan.borrower == address(0)) revert InvalidLoanId();
        if (loan.status != Types.LoanStatus.Requested) revert InvalidState();

        loan.status = Types.LoanStatus.Approved;

        emit LoanStatusChanged(loanId, Types.LoanStatus.Requested, Types.LoanStatus.Approved);
    }

    /**
     * @dev Process loan drawdown
     * @param loanId ID of the loan to drawdown
     */
    function drawdown(uint256 loanId) external onlyRole(POOL_ROLE) {
        Types.Loan storage loan = loans[loanId];
        if (loan.borrower == address(0)) revert InvalidLoanId();
        if (loan.status != Types.LoanStatus.Approved) revert InvalidState();

        loan.status = Types.LoanStatus.Drawn;
        loan.drawnAt = block.timestamp;
        loan.dueAt = block.timestamp + (loan.tenorDays * 1 days);

        emit LoanStatusChanged(loanId, Types.LoanStatus.Approved, Types.LoanStatus.Drawn);
    }

    /**
     * @dev Calculate current debt for a loan
     * @param loanId ID of the loan
     * @return principal Remaining principal
     * @return interest Accrued interest
     * @return lateFees Late fees (always 0 in MVP)
     * @return total Total debt
     */
    function currentDebt(uint256 loanId) external view returns (
        uint256 principal,
        uint256 interest,
        uint256 lateFees,
        uint256 total
    ) {
        Types.Loan storage loan = loans[loanId];
        if (loan.borrower == address(0)) revert InvalidLoanId();
        if (loan.status != Types.LoanStatus.Drawn) revert InvalidState();

        principal = loan.principal;
        interest = _calculateInterest(loan);
        lateFees = 0; // MVP: no late fees
        total = principal + interest;
    }

    /**
     * @dev Process loan repayment
     * @param loanId ID of the loan to repay
     * @param amount Amount being repaid
     * @return remaining Remaining debt after repayment
     */
    function repay(uint256 loanId, uint256 amount) external onlyRole(POOL_ROLE) returns (uint256 remaining) {
        if (amount == 0) revert AmountZero();

        Types.Loan storage loan = loans[loanId];
        if (loan.borrower == address(0)) revert InvalidLoanId();
        if (loan.status != Types.LoanStatus.Drawn) revert InvalidState();

        uint256 currentInterest = _calculateInterest(loan);
        uint256 totalDebt = loan.principal + currentInterest;

        uint256 interestPaid;
        uint256 principalPaid;

        if (amount >= totalDebt) {
            // Full repayment
            loan.status = Types.LoanStatus.Repaid;
            loan.principal = 0;
            remaining = 0;
            interestPaid = currentInterest;
            principalPaid = loan.principal;

            emit LoanStatusChanged(loanId, Types.LoanStatus.Drawn, Types.LoanStatus.Repaid);
        } else {
            // Partial repayment - prioritize interest
            interestPaid = amount <= currentInterest ? amount : currentInterest;
            principalPaid = amount - interestPaid;

            loan.principal -= principalPaid;
            remaining = totalDebt - amount;
        }

        emit LoanAccountingUpdated(loanId, interestPaid, principalPaid, remaining);
    }

    /**
     * @dev Mark a loan as defaulted
     * @param loanId ID of the loan to mark as defaulted
     */
    function markDefault(uint256 loanId) external onlyRole(POOL_ROLE) {
        Types.Loan storage loan = loans[loanId];
        if (loan.borrower == address(0)) revert InvalidLoanId();
        if (loan.status != Types.LoanStatus.Drawn) revert InvalidState();
        if (block.timestamp <= loan.dueAt) revert InvalidState();

        loan.status = Types.LoanStatus.Defaulted;

        emit LoanStatusChanged(loanId, Types.LoanStatus.Drawn, Types.LoanStatus.Defaulted);
    }

    /**
     * @dev Calculate accrued interest for a loan
     * @param loan Loan struct
     * @return Accrued interest amount
     */
    function _calculateInterest(Types.Loan storage loan) private view returns (uint256) {
        if (loan.drawnAt == 0) return 0;

        uint256 elapsedDays = (block.timestamp - loan.drawnAt) / 1 days;
        return (loan.principal * loan.rateBps * elapsedDays) / (10000 * 365);
    }

    /**
     * @dev Grant POOL_ROLE to a pool contract
     * @param pool Address of the pool contract
     */
    function grantPoolRole(address pool) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(POOL_ROLE, pool);
    }

    /**
     * @dev Revoke POOL_ROLE from a pool contract
     * @param pool Address of the pool contract
     */
    function revokePoolRole(address pool) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(POOL_ROLE, pool);
    }

    /**
     * @dev Get loan details
     * @param loanId ID of the loan
     * @return Loan struct
     */
    function getLoan(uint256 loanId) external view returns (Types.Loan memory) {
        return loans[loanId];
    }

    /**
     * @dev Get total loan count
     * @return Total number of loans created
     */
    function getLoanCount() external view returns (uint256) {
        return _loanCounter;
    }
}

contracts/MicroCreditPool.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./libs/Types.sol";
import "./interfaces/IPool.sol";
import "./interfaces/ILoanManager.sol";
import "./interfaces/ICreditScoreRegistry.sol";
import "./interfaces/ITreasury.sol";

/**
 * @title MicroCreditPool
 * @dev Main pool contract for MicroCredit DAO that manages deposits, loans, and LP shares
 */
contract MicroCreditPool is IPool, ERC20, ReentrancyGuard, Pausable, AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    // Immutable addresses
    IERC20 public immutable stable;
    ILoanManager public immutable loanManager;
    ICreditScoreRegistry public immutable creditScoreRegistry;
    ITreasury public immutable treasury;

    // Pool parameters
    Types.PoolParams public params;

    // Pool state
    uint256 public totalAssets;

    // Events
    event Deposited(address indexed user, uint256 amount, uint256 shares);
    event Withdrawn(address indexed user, uint256 shares, uint256 amount);
    event LoanRequested(uint256 indexed loanId, address indexed borrower, uint256 amount);
    event LoanApproved(uint256 indexed loanId);
    event LoanDrawn(uint256 indexed loanId, address indexed borrower, uint256 amount);
    event LoanRepaid(uint256 indexed loanId, address indexed payer, uint256 paidAmount);
    event LoanDefaulted(uint256 indexed loanId);
    event ParamsUpdated(Types.PoolParams oldParams, Types.PoolParams newParams);

    // Custom errors
    error AmountZero();
    error LowScore();
    error ExceedsPoolRatio();
    error InvalidState();
    error NotManager();
    error NothingToWithdraw();
    error InsufficientBalance();

    /**
     * @dev Constructor
     * @param _stable Address of the stablecoin (e.g., USDC)
     * @param _loanManager Address of the loan manager
     * @param _creditScoreRegistry Address of the credit score registry
     * @param _treasury Address of the treasury
     * @param _params Pool parameters
     * @param _name Name for LP token
     * @param _symbol Symbol for LP token
     */
    constructor(
        address _stable,
        address _loanManager,
        address _creditScoreRegistry,
        address _treasury,
        Types.PoolParams memory _params,
        string memory _name,
        string memory _symbol
    ) ERC20(_name, _symbol) {
        stable = IERC20(_stable);
        loanManager = ILoanManager(_loanManager);
        creditScoreRegistry = ICreditScoreRegistry(_creditScoreRegistry);
        treasury = ITreasury(_treasury);
        params = _params;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MANAGER_ROLE, msg.sender);
    }

    /**
     * @dev Deposit stablecoins and receive LP shares
     * @param amount Amount of stablecoins to deposit
     */
    function deposit(uint256 amount) external override nonReentrant whenNotPaused {
        if (amount == 0) revert AmountZero();

        uint256 shares;
        if (totalSupply() == 0) {
            shares = amount;
        } else {
            shares = (amount * totalSupply()) / totalAssets;
        }

        if (shares == 0) revert AmountZero();

        // Transfer stablecoins from user to pool
        stable.safeTransferFrom(msg.sender, address(this), amount);

        // Update state
        totalAssets += amount;

        // Mint LP shares
        _mint(msg.sender, shares);

        emit Deposited(msg.sender, amount, shares);
    }

    /**
     * @dev Withdraw stablecoins by burning LP shares
     * @param shares Amount of LP shares to burn
     */
    function withdraw(uint256 shares) external override nonReentrant whenNotPaused {
        if (shares == 0) revert AmountZero();
        if (balanceOf(msg.sender) < shares) revert InsufficientBalance();

        uint256 amount = (shares * totalAssets) / totalSupply();
        if (amount == 0) revert NothingToWithdraw();

        // Burn LP shares
        _burn(msg.sender, shares);

        // Update state
        totalAssets -= amount;

        // Transfer stablecoins to user
        stable.safeTransfer(msg.sender, amount);

        emit Withdrawn(msg.sender, shares, amount);
    }

    /**
     * @dev Request a loan from the pool
     * @param amount Amount of stablecoins requested
     * @return loanId ID of the created loan
     */
    function requestLoan(uint256 amount) external override whenNotPaused returns (uint256 loanId) {
        if (amount == 0) revert AmountZero();
        if (amount > totalAssets * params.maxLoanToPoolBps / 10000) revert ExceedsPoolRatio();

        // Check credit score if required
        if (params.minCreditScore > 0) {
            uint256 userScore = creditScoreRegistry.getScore(msg.sender);
            if (userScore < params.minCreditScore) revert LowScore();
        }

        // Create loan in loan manager
        loanId = loanManager.createLoan(msg.sender, amount, params.interestRateBps, params.tenorDays);

        emit LoanRequested(loanId, msg.sender, amount);
    }

    /**
     * @dev Approve a loan request (manager only)
     * @param loanId ID of the loan to approve
     */
    function approveLoan(uint256 loanId) external override onlyRole(MANAGER_ROLE) whenNotPaused {
        loanManager.approve(loanId);
        emit LoanApproved(loanId);
    }

    /**
     * @dev Process loan drawdown (borrower only)
     * @param loanId ID of the loan to drawdown
     */
    function drawdown(uint256 loanId) external override nonReentrant whenNotPaused {
        // Get loan details to verify borrower
        Types.Loan memory loan = loanManager.getLoan(loanId);
        if (loan.borrower != msg.sender) revert InvalidState();

        // Process drawdown in loan manager
        loanManager.drawdown(loanId);

        // Transfer stablecoins to borrower
        stable.safeTransfer(msg.sender, loan.principal);

        // Update pool state
        totalAssets -= loan.principal;

        emit LoanDrawn(loanId, msg.sender, loan.principal);
    }

    /**
     * @dev Repay a loan
     * @param loanId ID of the loan to repay
     * @param amount Amount of stablecoins to repay
     * @return remaining Remaining debt after repayment
     */
    function repay(uint256 loanId, uint256 amount) external override nonReentrant whenNotPaused returns (uint256 remaining) {
        if (amount == 0) revert AmountZero();

        // Transfer stablecoins from user to pool
        stable.safeTransferFrom(msg.sender, address(this), amount);

        // Get debt breakdown before repayment
        (uint256 principalBefore, uint256 interestBefore, , uint256 totalBefore) = loanManager.currentDebt(loanId);

        // Process repayment in loan manager
        remaining = loanManager.repay(loanId, amount);

        // Get debt breakdown after repayment
        (uint256 principalAfter, uint256 interestAfter, , ) = loanManager.currentDebt(loanId);

        // Calculate amounts paid
        uint256 principalPaid = principalBefore - principalAfter;
        uint256 interestPaid = interestBefore - interestAfter;

        // Calculate treasury fee
        uint256 feeInterest = (interestPaid * params.reserveFactorBps) / 10000;

        // Send fee to treasury
        if (feeInterest > 0) {
            stable.safeApprove(address(treasury), feeInterest);
            treasury.receiveFees(address(stable), feeInterest);
        }

        // Update pool state with net amounts
        uint256 netInterest = interestPaid - feeInterest;
        totalAssets += principalPaid + netInterest;

        // Check if loan is fully repaid
        if (remaining == 0) {
            // Increase borrower's credit score
            creditScoreRegistry.increaseScore(msg.sender, 15);
            emit LoanRepaid(loanId, msg.sender, amount);
        }
    }

    /**
     * @dev Mark a loan as defaulted (manager only)
     * @param loanId ID of the loan to mark as defaulted
     */
    function markDefault(uint256 loanId) external override onlyRole(MANAGER_ROLE) whenNotPaused {
        // Get loan details
        Types.Loan memory loan = loanManager.getLoan(loanId);

        // Mark as defaulted in loan manager
        loanManager.markDefault(loanId);

        // Decrease borrower's credit score
        creditScoreRegistry.decreaseScore(loan.borrower, 30);

        emit LoanDefaulted(loanId);
    }

    /**
     * @dev Get current pool parameters
     * @return Pool parameters
     */
    function getParams() external view override returns (Types.PoolParams memory) {
        return params;
    }

    /**
     * @dev Update pool parameters (manager only)
     * @param newParams New pool parameters
     */
    function setParams(Types.PoolParams calldata newParams) external onlyRole(MANAGER_ROLE) {
        Types.PoolParams memory oldParams = params;
        params = newParams;

        emit ParamsUpdated(oldParams, newParams);
    }

    /**
     * @dev Pause the pool (manager only)
     */
    function pause() external onlyRole(MANAGER_ROLE) {
        _pause();
    }

    /**
     * @dev Unpause the pool (manager only)
     */
    function unpause() external onlyRole(MANAGER_ROLE) {
        _unpause();
    }

    /**
     * @dev Grant MANAGER_ROLE to an address
     * @param manager Address to grant the role to
     */
    function grantManagerRole(address manager) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(MANAGER_ROLE, manager);
    }

    /**
     * @dev Revoke MANAGER_ROLE from an address
     * @param manager Address to revoke the role from
     */
    function revokeManagerRole(address manager) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(MANAGER_ROLE, manager);
    }

    /**
     * @dev Get total assets in the pool
     * @return Total assets amount
     */
    function getTotalAssets() external view returns (uint256) {
        return totalAssets;
    }

    /**
     * @dev Calculate shares for a given amount of stablecoins
     * @param amount Amount of stablecoins
     * @return shares Corresponding LP shares
     */
    function calculateShares(uint256 amount) external view returns (uint256 shares) {
        if (totalSupply() == 0) {
            shares = amount;
        } else {
            shares = (amount * totalSupply()) / totalAssets;
        }
    }

    /**
     * @dev Calculate stablecoins for a given amount of LP shares
     * @param shares Amount of LP shares
     * @return amount Corresponding stablecoins
     */
    function calculateAmount(uint256 shares) external view returns (uint256 amount) {
        if (totalSupply() == 0) {
            amount = 0;
        } else {
            amount = (shares * totalAssets) / totalSupply();
        }
    }
}

contracts/MicroCreditPoolFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./MicroCreditPool.sol";
import "./libs/Types.sol";

/**
 * @title MicroCreditPoolFactory
 * @dev Factory contract for creating MicroCredit pools
 */
contract MicroCreditPoolFactory is AccessControl {
    // Array of all created pools
    address[] public pools;

    // Mapping from pool address to creation info
    mapping(address => bool) public isPool;

    // Events
    event PoolCreated(
        address indexed pool,
        address indexed stable,
        Types.PoolParams params
    );

    // Custom errors
    error InvalidAddress();
    error PoolAlreadyExists();

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Create a new MicroCredit pool
     * @param stable Address of the stablecoin (e.g., USDC)
     * @param params Pool configuration parameters
     * @param name Name for the LP token
     * @param symbol Symbol for the LP token
     * @return pool Address of the created pool
     */
    function createPool(
        address stable,
        Types.PoolParams calldata params,
        string memory name,
        string memory symbol
    ) external onlyRole(DEFAULT_ADMIN_ROLE) returns (address pool) {
        if (stable == address(0)) revert InvalidAddress();
        if (bytes(name).length == 0 || bytes(symbol).length == 0) revert InvalidAddress();

        // Validate pool parameters
        if (params.interestRateBps > 10000) revert InvalidAddress(); // Max 100% APR
        if (params.tenorDays == 0 || params.tenorDays > 3650) revert InvalidAddress(); // Max 10 years
        if (params.maxLoanToPoolBps > 10000) revert InvalidAddress(); // Max 100% of pool
        if (params.reserveFactorBps > 10000) revert InvalidAddress(); // Max 100% of interest
        if (params.minCreditScore > 100) revert InvalidAddress(); // Max score 100

        // Create pool contract
        pool = address(new MicroCreditPool(
            stable,
            address(this), // Factory will be the loan manager initially
            address(this), // Factory will be the credit score registry initially
            address(this), // Factory will be the treasury initially
            params,
            name,
            symbol
        ));

        // Register pool
        pools.push(pool);
        isPool[pool] = true;

        emit PoolCreated(pool, stable, params);
    }

    /**
     * @dev Get all created pools
     * @return Array of pool addresses
     */
    function getPools() external view returns (address[] memory) {
        return pools;
    }

    /**
     * @dev Get pool count
     * @return Total number of pools created
     */
    function getPoolCount() external view returns (uint256) {
        return pools.length;
    }

    /**
     * @dev Check if an address is a valid pool
     * @param pool Address to check
     * @return True if the address is a valid pool
     */
    function isValidPool(address pool) external view returns (bool) {
        return isPool[pool];
    }

    /**
     * @dev Grant DEFAULT_ADMIN_ROLE to an address
     * @param admin Address to grant the role to
     */
    function grantAdminRole(address admin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (admin == address(0)) revert InvalidAddress();
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }

    /**
     * @dev Revoke DEFAULT_ADMIN_ROLE from an address
     * @param admin Address to revoke the role from
     */
    function revokeAdminRole(address admin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (admin == msg.sender) revert InvalidAddress(); // Cannot revoke own role
        _revokeRole(DEFAULT_ADMIN_ROLE, admin);
    }

    /**
     * @dev Pause all pools (emergency function)
     * @param poolAddresses Array of pool addresses to pause
     */
    function pausePools(address[] calldata poolAddresses) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < poolAddresses.length; i++) {
            address pool = poolAddresses[i];
            if (isPool[pool]) {
                MicroCreditPool(pool).pause();
            }
        }
    }

    /**
     * @dev Unpause all pools
     * @param poolAddresses Array of pool addresses to unpause
     */
    function unpausePools(address[] calldata poolAddresses) external onlyRole(DEFAULT_ADMIN_ROLE) {
        for (uint256 i = 0; i < poolAddresses.length; i++) {
            address pool = poolAddresses[i];
            if (isPool[pool]) {
                MicroCreditPool(pool).unpause();
            }
        }
    }
}

contracts/Treasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Treasury
 * @dev Manages fees received from MicroCredit pools
 */
contract Treasury is AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant POOL_ROLE = keccak256("POOL_ROLE");
    bytes32 public constant TREASURY_ADMIN_ROLE = keccak256("TREASURY_ADMIN_ROLE");

    // Events
    event FeesReceived(address indexed token, uint256 amount);
    event Claimed(address indexed token, address indexed to, uint256 amount);

    // Custom errors
    error AmountZero();
    error InsufficientBalance();
    error TransferFailed();

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(TREASURY_ADMIN_ROLE, msg.sender);
    }

    /**
     * @dev Receive fees from a pool
     * @param token Address of the token being received
     * @param amount Amount of tokens received
     */
    function receiveFees(address token, uint256 amount) external onlyRole(POOL_ROLE) {
        if (amount == 0) revert AmountZero();

        // Transfer tokens from the pool to this treasury
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);

        emit FeesReceived(token, amount);
    }

    /**
     * @dev Get the balance of a specific token in the treasury
     * @param token Address of the token
     * @return Balance amount
     */
    function balanceOf(address token) external view returns (uint256) {
        return IERC20(token).balanceOf(address(this));
    }

    /**
     * @dev Claim tokens from the treasury
     * @param token Address of the token to claim
     * @param to Address to send the tokens to
     * @param amount Amount of tokens to claim
     */
    function claim(address token, address to, uint256 amount) external onlyRole(TREASURY_ADMIN_ROLE) {
        if (amount == 0) revert AmountZero();

        uint256 balance = IERC20(token).balanceOf(address(this));
        if (amount > balance) revert InsufficientBalance();

        IERC20(token).safeTransfer(to, amount);

        emit Claimed(token, to, amount);
    }

    /**
     * @dev Grant POOL_ROLE to a pool contract
     * @param pool Address of the pool contract
     */
    function grantPoolRole(address pool) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(POOL_ROLE, pool);
    }

    /**
     * @dev Revoke POOL_ROLE from a pool contract
     * @param pool Address of the pool contract
     */
    function revokePoolRole(address pool) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(POOL_ROLE, pool);
    }

    /**
     * @dev Grant TREASURY_ADMIN_ROLE to an address
     * @param admin Address to grant the role to
     */
    function grantTreasuryAdminRole(address admin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _grantRole(TREASURY_ADMIN_ROLE, admin);
    }

    /**
     * @dev Revoke TREASURY_ADMIN_ROLE from an address
     * @param admin Address to revoke the role from
     */
    function revokeTreasuryAdminRole(address admin) external onlyRole(DEFAULT_ADMIN_ROLE) {
        _revokeRole(TREASURY_ADMIN_ROLE, admin);
    }
}

contracts/VNDToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract VNDToken is ERC20 {
	// 1. Token Basics
	uint256 public constant MAX_SUPPLY = 50_000_000 * 10 ** 18; // 50 million tokens

	// 2. Charity System
	mapping(address => bool) public verifiedCharities;
	mapping(address => uint256) public donationTaxDeductionRates; // 500 = 5%

	// 3. Events
	event CharityRegistered(address charity, uint256 taxDeductionRate);
	event DonationMade(
		address donor,
		address charity,
		uint256 amount,
		uint256 taxCredit
	);

	// 4. Initialization
	constructor() ERC20("Vietnamese Dong", "VND") {
		// _mint(msg.sender, MAX_SUPPLY); // Create all tokens for deployer
	}

	// 5. Core Functions
	function transfer(
		address to,
		uint256 amount
	) public override returns (bool) {
		_transfer(msg.sender, to, amount);
		return true;
	}

	// 6. Charity Functions
	function donateToCharity(
		address charity,
		uint256 amount
	) public returns (bool) {
		require(verifiedCharities[charity], "Not a verified charity");
		uint256 taxCredit = (amount * donationTaxDeductionRates[charity]) /
			10000;

		_transfer(msg.sender, charity, amount);
		_mint(msg.sender, taxCredit);

		emit DonationMade(msg.sender, charity, amount, taxCredit);
		return true;
	}

	function registerCharity(address charity, uint256 taxDeductionRate) public {
		require(taxDeductionRate <= 1000, "Deduction rate too high (max 10%)");
		verifiedCharities[charity] = true;
		donationTaxDeductionRates[charity] = taxDeductionRate;
		emit CharityRegistered(charity, taxDeductionRate);
	}

	// 7. Utility Functions
	function getCirculatingSupply() public view returns (uint256) {
		return totalSupply();
	}

	// Minting (open to anyone but can't exceed max supply)
	function mint(address to, uint256 amount) public {
		require(
			totalSupply() + amount <= MAX_SUPPLY,
			"VND: Exceeds max supply"
		);
		_mint(to, amount);
	}

	function burn(uint256 amount) public {
		_burn(msg.sender, amount);
	}
}

